    var uv: vec2f = vec2f(avgDir.z * 0.5 + 0.5, formFactor);
    uv = uv * LUT_SCALE_disk + LUT_BIAS_disk;

    let scale: f32 = textureSampleLevel(areaLightsLutTex2, areaLightsLutTex2Sampler, uv, 0.0).w;

